          common.add(state, "baseTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
            material.baseTexture = v === "None" ? null : _this6.textures[v] || originTexture.baseTexture;
          });
          common.add(state, "normalTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
            material.normalTexture = v === "None" ? null : _this6.textures[v] || originTexture.normalTexture;
          });
          common.add(state, "emissiveTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
            material.emissiveTexture = v === "None" ? null : _this6.textures[v] || originTexture.emissiveTexture;
          });
          common.add(state, "occlusionTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
            material.occlusionTexture = v === "None" ? null : _this6.textures[v] || originTexture.occlusionTexture;
          });
          common.add(state, "opacityTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
            material.opacityTexture = v === "None" ? null : _this6.textures[v] || originTexture.opacityTexture;
          });
          common.open();
        }
      });
    };

    _proto.loadAnimationGUI = function loadAnimationGUI(animations) {
      if (this.animationFolder) {
        this.gui.removeFolder(this.animationFolder);
        this.animationFolder = null;
      }

      if (animations != null && animations.length) {
        this.animationFolder = this.gui.addFolder("Animation");
        this.animationFolder.open();
        var animator = this.gltfRootEntity.getComponent(_oasisEngine.Animation);
        animator.playAnimationClip(animations[0].name);
        var state = {
          animation: animations[0].name
        };
        this.animationFolder.add(state, "animation", ["None"].concat(animations.map(function (animation) {
          return animation.name;
        }))).onChange(function (name) {
          if (name === "None") {
            animator.stop(true);
          } else {
            animator.playAnimationClip(name);
          }
        });
      }
    };

    return Oasis;
  }();

  new Oasis();
});

