          baseColor: Oasis.colorToGui(material.baseColor),
          emissiveColor: Oasis.colorToGui(material.emissiveColor),
          specularColor: Oasis.colorToGui(material.specularColor),
          baseTexture: material.baseTexture ? "origin" : "",
          roughnessMetallicTexture: material.roughnessMetallicTexture ? "origin" : "",
