          occlusionTexture: material.occlusionTexture ? "origin" : "",
          specularGlossinessTexture: material.specularGlossinessTexture ? "origin" : ""
        };
        var originTexture = {
          baseTexture: material.baseTexture,
          roughnessMetallicTexture: material.roughnessMetallicTexture,
          normalTexture: material.normalTexture,
          emissiveTexture: material.emissiveTexture,
          occlusionTexture: material.occlusionTexture,
          specularGlossinessTexture: material.specularGlossinessTexture
        };
        var f = folder.addFolder(folderName[material.name] ? material.name + "_" + (folderName[material.name] + 1) : material.name);
        folderName[material.name] = folderName[material.name] == null ? 1 : folderName[material.name] + 1; // metallic

        if (material instanceof _oasisEngine.PBRMaterial) {
          var mode1 = f.addFolder("");
          mode1.add(material, "metallic", 0, 1).step(0.01);
          mode1.add(material, "roughness", 0, 1).step(0.01);
          mode1.add(state, "roughnessMetallicTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
