          });
          mode1.open();
        } // specular
        else if (material instanceof _oasisEngine.PBRSpecularMaterial) {
          var mode2 = f.addFolder("");
          mode2.add(material, "glossiness", 0, 1).step(0.01);
          mode2.addColor(state, "specularColor").onChange(function (v) {
            Oasis.guiToColor(v, material.specularColor);
          });
          mode2.add(state, "specularGlossinessTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
