          });
          common.add(state, "occlusionTexture", ["None", "origin"].concat(Object.keys(_this6.textures))).onChange(function (v) {
            material.occlusionTexture = v === "None" ? null : _this6.textures[v] || originTexture.occlusionTexture;
          });
          common.open();
        }
      });
    };

    _proto.loadAnimationGUI = function loadAnimationGUI(animations) {
      if (this.animationFolder) {
        this.gui.removeFolder(this.animationFolder);
        this.animationFolder = null;
      }

      if (animations != null && animations.length) {
        this.animationFolder = this.gui.addFolder("Animation");
        this.animationFolder.open();
        var animator = this.gltfRootEntity.getComponent(_oasisEngine.Animator);
        animator.play(animations[0].name);
        var state = {
          animation: animations[0].name
        };
        this.animationFolder.add(state, "animation", ["None"].concat(animations.map(function (animation) {
          return animation.name;
        }))).onChange(function (name) {
          if (name === "None") {
            animator.speed = 0;
          } else {
            animator.speed = 1;
            animator.play(name);
          }
        });
      }
    };

    return Oasis;
  }();

  new Oasis();
